
public class Ejercicio6a {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/** Utilizar el tipo de dato entero para crear al menos 5 variables 
		 * con numeros octales y hexadecimales.*/

		// Los octales comienzan con 0
		int varOctal1 = 01; //Dec 1
		int varOctal2 = 07; //Dec 7
		int varOctal3 = 010; //Dec 8
		int varOctal4 = 015; //Dec 13
		
		// Los Hexadecimal comienzan con 0x
		int varHexa5 = 0xF;
		int varHexa6 = 0x1;
		int varHexa7 = 0x1A;
		
		System.out.print("Ocales en decimales");
		System.out.print("\n" + varOctal1);
		System.out.print("\n" + varOctal2);
		System.out.print("\n" + varOctal3);
		System.out.print("\n" + varOctal4);
		System.out.print("\nHexadecimales en decimales");
		System.out.print("\n" + varHexa5);
		System.out.print("\n" + varHexa6);
		System.out.print("\n" + varHexa7);
		
	}

}
