
public class Ejercicio8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/* Realizar un programa que realice las operaciones basicas de suma, 
		 * resta, producto y división de dos numeros. Hacer con numeros 
		 * enteros, y decimales.*/
		
		int entero1 = 10;
		int entero2 = 5;
		int resultado1;
		
		double real1 = 15.60;
		double real2 = 20.80;
		float resultado2;
		
		System.out.print("\n Operaciones con enteros. entero1 = 10 y entero2 = 5");
		System.out.print("\n SUMA ==> " + (entero1+entero2));
		System.out.print("\n RESTA ==> " + (entero1-entero2));
		System.out.print("\n MULTI ==> " + (entero1*entero2));
		System.out.print("\n DIVI ==> " + (entero1/entero2));
		
		System.out.print("\n Operaciones con reales (double)" + real1 + " y " + real2);
		System.out.print("\n SUMA ==> " + (real1+real2));
		System.out.print("\n RESTA ==> " + (real1-real2));
		System.out.print("\n MULTI ==> " + (real1*real2));
		System.out.print("\n DIVI ==> " + (real1/real2));

		
	}
	

}
